import { useForm } from "react-hook-form";
import "./App.css";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";

import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Row from 'react-bootstrap/Row';

// Тип для формы
interface UserInput {
  fullName: string;
  food: string;
  flag: boolean;
}

// Дефолтные значения для формы
const defaultValues: UserInput = {
  fullName: "Кек Кекович",
  food: "",
  flag: true,
};

// Правила проверки из библиотеки yup
const validationSchema = yup.object({
  fullName: yup
      .string()
      .required("Обязательное поле")
      .min(2, "Имя должно содержать 2-100 симовлов")
      .max(100, "Имя должно содержать 2-100 симовлов"),
  food: yup
      .string().
      required("Выберите еду"),
  flag: yup
      .boolean()
      .isTrue("Вы должны согласиться")
      .required("Требуется одобрение"),
});

function App() {
  const {
    // Зарегистрировать элемент - позволяет зарегистрировать элемент и применить соответствующие правила проверки.
    register,
    // Обработка формы отправки - получит данные формы, если проверка прошла успешно
    handleSubmit,
    // Ошибки формы - показываает ошибки
    formState: { errors },
    // Сбор полей до пустых или дефолнтных
    reset,
    //useForm - хук из react-hook-form
  } = useForm<UserInput>({
    // Дефолтные значения для формы
    defaultValues,
    // Мод, который врубает onChanage (дейтсиве на отпраку) и onBlur (действие на изменение формы)
    mode: "all",
    //костыль в виде any???
    resolver: yupResolver<any>(validationSchema, undefined, {raw: true}),
  });

  const onSubmitHandler = (values: UserInput) => {
    console.log(`Submitted`);
    console.log({values});
    reset();
  };

  return (
      <main>
        <h1>Форма заказа еды</h1>
        <form onSubmit={handleSubmit(onSubmitHandler)}>
          <div>
            <label htmlFor="fullName">Имя</label>
            <input {...register("fullName")} id="fullName" type="text" />
            {errors.fullName && (
                <p>{errors.fullName.message}</p>
            )}
          </div>

          <div>
            <label htmlFor="food">Выбор еды</label>
            <select {...register("food")} id="food">
              <option value="" disabled>
                Выберите...
              </option>
              <option value="pizza">Пицца</option>
              <option value="burger">Бургер</option>
              <option value="ice-cream">Ролл</option>
            </select>            {errors.food && (
                <p>{errors.food.message}</p>
            )}
          </div>

          <div>
            <input
                {...register("flag")}
                id="flag"
                type="checkbox"
          />
            <label htmlFor="flag">
              Вы согласны заказать?
            </label>
            {errors.flag && (
                <p>{errors.flag.message}</p>
            )}
          </div>

          <button type="submit">Заказать</button>
        </form>
        <Form noValidate validated={validated} onSubmit={handleSubmit}>
          <Row className="mb-3">
            <Form.Group as={Col} md="4" controlId="validationCustom01">
              <Form.Label>First name</Form.Label>
              <Form.Control
                  required
                  type="text"
                  placeholder="First name"
                  defaultValue="Mark"
              />
              <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
            </Form.Group>
            <Form.Group as={Col} md="4" controlId="validationCustom02">
              <Form.Label>Last name</Form.Label>
              <Form.Control
                  required
                  type="text"
                  placeholder="Last name"
                  defaultValue="Otto"
              />
              <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
            </Form.Group>
            <Form.Group as={Col} md="4" controlId="validationCustomUsername">
              <Form.Label>Username</Form.Label>
              <InputGroup hasValidation>
                <InputGroup.Text id="inputGroupPrepend">@</InputGroup.Text>
                <Form.Control
                    type="text"
                    placeholder="Username"
                    aria-describedby="inputGroupPrepend"
                    required
                />
                <Form.Control.Feedback type="invalid">
                  Please choose a username.
                </Form.Control.Feedback>
              </InputGroup>
            </Form.Group>
          </Row>
          <Row className="mb-3">
            <Form.Group as={Col} md="6" controlId="validationCustom03">
              <Form.Label>City</Form.Label>
              <Form.Control type="text" placeholder="City" required />
              <Form.Control.Feedback type="invalid">
                Please provide a valid city.
              </Form.Control.Feedback>
            </Form.Group>
            <Form.Group as={Col} md="3" controlId="validationCustom04">
              <Form.Label>State</Form.Label>
              <Form.Control type="text" placeholder="State" required />
              <Form.Control.Feedback type="invalid">
                Please provide a valid state.
              </Form.Control.Feedback>
            </Form.Group>
            <Form.Group as={Col} md="3" controlId="validationCustom05">
              <Form.Label>Zip</Form.Label>
              <Form.Control type="text" placeholder="Zip" required />
              <Form.Control.Feedback type="invalid">
                Please provide a valid zip.
              </Form.Control.Feedback>
            </Form.Group>
          </Row>
          <Form.Group className="mb-3">
            <Form.Check
                required
                label="Agree to terms and conditions"
                feedback="You must agree before submitting."
                feedbackType="invalid"
            />
          </Form.Group>
          <Button type="submit">Submit form</Button>
        </Form>
      </main>
  );
}

export default App;
