import { useForm } from "react-hook-form";
import "./App.css";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";

// Тип для формы
interface UserInput {
  fullName: string;
  food: string;
  approvesTutorial: boolean;
}

// Дефолтный тип для формы
const defaultValues: UserInput = {
  fullName: "Кек Кекович",
  food: "",
  approvesTutorial: true,
};

// Правила проверки из библиотеки yup
const validationSchema = yup.object({
  fullName: yup
      .string()
      .required("Обязательное поле")
      .min(2, "Имя должно содержать 2-100 симовлов")
      .max(100, "Имя должно содержать 2-100 симовлов"),
  food: yup
      .string().
      required("Выберите еду"),
  approvesTutorial: yup
      .boolean()
      .isTrue("Вы должны согласиться")
      .required("Требуется одобрение"),
});

function App() {
  const {
    // Зарегистрировать входы
    register,
    handleSubmit,
    formState: { errors }, // get errors of the form
  } = useForm<UserInput>({
    defaultValues,
    resolver: yupResolver<any>(validationSchema), //костыль в виде any???
    mode: "onTouched", // default is "onSubmit"
  });

  const onSubmitHandler = (values: UserInput) => {
    console.log(`Submitted`);
    console.table(values);
  };

  return (
      <main className="main prose">
        <h1>Forms</h1>

        <form onSubmit={handleSubmit(onSubmitHandler)} className="form">
          <div>
            <label htmlFor="fullName">Full Name</label>
            <input {...register("fullName")} id="fullName" type="text" />
            {errors.fullName && (
                <p className="error-message">{errors.fullName.message}</p>
            )}
          </div>

          <div>
            <label htmlFor="food">Select Your Favourite Food!</label>
            <select {...register("food")} id="food">
              <option value="" disabled>
                Please Select...
              </option>
              <option value="pizza">Pizza</option>
              <option value="burger">Burger</option>
              <option value="ice-cream">Ice Cream</option>
            </select>
            {errors.food && (
                <p className="error-message">{errors.food.message}</p>
            )}
          </div>

          <div>
            <input
                {...register("approvesTutorial")}
                id="approves-tutorial"
                type="checkbox"
            />
            <label htmlFor="approves-tutorial">
              Do you approve this tutorial?
            </label>
            {errors.approvesTutorial && (
                <p className="error-message">{errors.approvesTutorial.message}</p>
            )}
          </div>

          <button type="submit">Submit</button>
        </form>
      </main>
  );
}

export default App;
